INSERT INTO `wp_options` VALUES (207,'_site_transient_wpmdb_help_message','<style type=\"text/css\" media=\"screen\">body .support .support-content{overflow:hidden;width:727px}body .support .support-content .intro{margin-bottom:20px}body .support .support-content .submission-error p,body .support .support-content .submission-success p{padding:2px;margin:.5em 0;font-size:13px;line-height:1.5}body .support .support-content .dbrains-support-form{width:475px;float:left}body .support .support-content .dbrains-support-form p{width:auto}body .support .support-content .dbrains-support-form .field{margin-bottom:5px}body .support .support-content .dbrains-support-form input[type=text],body .support .support-content .dbrains-support-form textarea{width:100%}body .support .support-content .dbrains-support-form .field.from label{float:left;line-height:28px;display:block;font-weight:700}body .support .support-content .dbrains-support-form .field.from select{float:right;width:400px}body .support .support-content .dbrains-support-form .field.from .note{clear:both;padding-top:5px}body .support .support-content .dbrains-support-form .field.email-message textarea{height:170px}body .support .support-content .dbrains-support-form .field.remote-diagnostic-content{padding-left:20px}body .support .support-content .dbrains-support-form .field.remote-diagnostic-content ol{margin:0 0 5px 20px}body .support .support-content .dbrains-support-form .field.remote-diagnostic-content li{font-size:12px;color:#666;margin-bottom:0;line-height:1.4em}body .support .support-content .dbrains-support-form .field.remote-diagnostic-content textarea{height:80px}body .support .support-content .dbrains-support-form .note{font-size:12px;color:#666}body .support .support-content .dbrains-support-form .submit-form{overflow:hidden;padding:10px 0}body .support .support-content .dbrains-support-form .button{float:left}body .support .support-content .dbrains-support-form .button:active,body .support .support-content .dbrains-support-form .button:focus{outline:0}body .support .support-content .dbrains-support-form .ajax-spinner{float:left;margin-left:5px;margin-top:3px}body .support .support-content .additional-help{float:right;width:220px}body .support .support-content .additional-help a{text-decoration:none}body .support .support-content .additional-help h1{margin:0 0 12px;padding:0;font-size:18px;font-weight:400;line-height:1}body .support .support-content .additional-help h1 a{color:#333}body .support .support-content .additional-help .docs{background-color:#e6e6e6;padding:15px 15px 10px}body .support .support-content .additional-help .docs ul{margin:0}body .support .support-content .additional-help .docs li{font-size:14px}</style><section class=\"dbrains-support-form\">\n\n<p class=\"intro\">\n	You have an active <strong>Developer</strong> license. You will get front-of-the-line email support service when submitting the form below.</p>\n\n<div class=\"updated submission-success\" style=\"display: none;\">\n	<p><strong>Success!</strong> &mdash; Thanks for submitting your support request. We\'ll be in touch soon.</p>\n</div>\n\n<div class=\"error submission-error api-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; </p>\n</div>\n\n<div class=\"error submission-error xhr-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; There was a problem submitting your request:</p>\n</div>\n\n<div class=\"error submission-error email-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; Please select your email address.</p>\n</div>\n\n<div class=\"error submission-error subject-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; Please enter a subject.</p>\n</div>\n\n<div class=\"error submission-error message-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; Please enter a message.</p>\n</div>\n\n<div class=\"error submission-error remote-diagnostic-content-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; Please paste in the Diagnostic Info &amp; Error Log from your <strong>remote site</strong>.</p>\n</div>\n\n<div class=\"error submission-error both-diagnostic-same-error\" style=\"display: none;\">\n	<p><strong>Error!</strong> &mdash; Looks like you pasted the local Diagnostic Info &amp; Error Log into the textbox for the remote info. Please get the info for your <strong>remote site</strong> and paste it in, or just uncheck the second checkbox if you&#8217;d rather not include your remote site info.</p>\n</div>\n\n<form target=\"_blank\" method=\"post\" action=\"https://api.deliciousbrains.com/?wc-api=delicious-brains&request=submit_support_request&licence_key=40df3e2e-84de-44de-80c5-0b0d971bf701&product=wp-migrate-db-pro\">\n\n	<div class=\"field from\">\n		<label>From:</label>\n		<select name=\"email\">\n		<option value=\"\">&mdash; Select your email address &mdash;</option>\n		<option value=\"jakub@bracketspace.com\">jakub@bracketspace.com</option>		</select>\n\n		<p class=\"note\">\n			Replies will be sent to this email address. Update your name &amp; email in <a href=\"https://deliciousbrains.com/my-account/\">My Account</a>.		</p>\n	</div>\n\n	<div class=\"field subject\">\n		<input type=\"text\" name=\"subject\" placeholder=\"Subject\">\n	</div>\n\n	<div class=\"field email-message\">\n		<textarea name=\"message\" placeholder=\"Message\"></textarea>\n	</div>\n\n	<div class=\"field checkbox local-diagnostic\">\n		<label>\n			<input type=\"checkbox\" name=\"local-diagnostic\" value=\"1\" checked>\n			Attach <strong>this site&#8217;s</strong> Diagnostic Info &amp; Error Log (below)		</label>\n	</div>\n		<div class=\"field checkbox remote-diagnostic\">\n		<label>\n			<input type=\"checkbox\" name=\"remote-diagnostic\" value=\"1\" checked>\n			Attach the <strong>remote site&#8217;s</strong> Diagnostic Info &amp; Error Log		</label>\n	</div>\n\n	<div class=\"field remote-diagnostic-content\">\n		<ol>\n			<li>Go to the Help tab of the remote site</li>\n			<li>Copy the Diagnostic Info &amp; Error Log</li>\n			<li>Paste it below</li>\n		</ol>\n		<textarea name=\"remote-diagnostic-content\" placeholder=\"Remote site&#8217;s Diagnostic Info &amp; Error Log\"></textarea>\n	</div>\n		<div class=\"submit-form\">\n		<button type=\"submit\" class=\"button\">Send Email</button>\n	</div>\n\n	<p class=\"note trouble\">\n		Having trouble submitting the form? Email your support request to <a href=\"mailto:priority-wpmdb@deliciousbrains.com\">priority-wpmdb@deliciousbrains.com</a> instead.	</p>\n\n</form>\n\n</section>\n\n	<aside class=\"additional-help\">\n		<section class=\"docs\">\n			<h1><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/\">Documentation</a></h1>\n			<ul class=\"categories\">\n				<li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/getting-started/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medium=insideplugin\">Getting Started</a></li><li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/debugging/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medium=insideplugin\">Debugging</a></li><li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/cli/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medium=insideplugin\">CLI</a></li><li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/common-errors/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medium=insideplugin\">Common Errors</a></li><li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/howto/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medium=insideplugin\">How To</a></li><li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/addons/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medium=insideplugin\">Addons</a></li><li><a href=\"https://deliciousbrains.com/wp-migrate-db-pro/docs/multisite/?utm_source=MDB%2BPaid&#038;utm_campaign=support%2Bdocs&#038;utm_medi